import { useParams } from "react-router-dom";
import '../style.scss'

const Detail = (data:any) => {
    const { musicId } = useParams();

    console.log(musicId)
    console.log(data)
    const DetailData = data.data.filter((data:any)=> (
        data.id.attributes["im:id"] === musicId
    ))
    return (
        <div className="detail">

            <div>{DetailData[0]["im:name"].label}</div>
            <div className="정보들">
                <img src={DetailData[0]["im:image"][0].label}></img>
                <div>
                    <div>가격 : {DetailData[0]["im:price"].label}</div>
                    <div>아티스트 : {DetailData[0]["im:artist"].label}</div>
                    <div>발매일 : {DetailData[0]["im:releaseDate"].label}</div>
                    <div>{DetailData[0]["im:price"].attributes.amout}</div>
                    {/* <a href="{DetailData[0].link.href}">더보기</a> */}
                </div>
                아티스트
                장르

            </div>

        </div>
    )
}

export default Detail;
