import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import '../style.scss'

const Chat = () => {
    const [data, setData] = useState<any[]>([]);
    const [search, setSearch] = useState("");
    useEffect(() => {
      axios
        .get ('https://itunes.apple.com/us/rss/topalbums/limit=100/json',)
        .then((res) => {
            setData(res.data.feed.entry)
        })
        .catch((err) => {
            console.log(err)
        })  
    },[])
    console.log(data)

    return(
        <div className="chat">
            MUSIC TOP 100
            <div className="search">
                <input
                    placeholder="찾으시는 음악 이름을 입력하세요"

                />
                <button >검색</button>
            </div>
            <div className="sort">
                <button>오름차순</button>
                <button >내림차순</button>
            </div>
            <div className="musics">
            <div>순위</div>
            {data.map((music:any) => {
                return(
                    <Link to={`/detail/${music.id.attributes["im:id"]}`}>
                        {/* <div>{music}</div><div className="musics"> */}
                        <div className="musicInfo">
                            {/* <div>{setNumber(number+1)}</div> */}
                            <img className="img" src={music["im:image"][0].label}></img>
                            <div className="name">{music.title.label}</div>
                        </div>   
                    </Link>
                )
            })}
            </div>
        </div>
    )
}

export default Chat;
