import { useParams } from "react-router-dom";
import '../style.scss'

const Detail = (data:any) => {
    const { musicId } = useParams();
    // const [data, setData] = useState();

    console.log(musicId)
    console.log(data)

    // useEffect(() => {
    //     const savedData = changeData();
