import axios from "axios";
import { useEffect, useState } from "react";
import '../style.scss'

const Chat = () => {
    const [data, setData] = useState([]);
    useEffect(() => {
      axios
        .get ('https://itunes.apple.com/us/rss/topalbums/limit=100/json',)
        .then((res) => {
            setData(res.data.feed.entry)
            console.log(res.data.feed.entry[0])
        })
        .catch((err) => {
            console.log(err)
        })  
    },[])

    const searchMusic = () => {
      console.log() 
    }
    return(
        <div className="chat">
            <div className="search">
                <input
                    placeholder="검색어를 입력하세요"
                    onChange={searchMusic}
                />
                <button>검색</button>
            </div>

            {data && data.map((music:any) => {
                return(
                    // <div>{music}</div><div className="musics">
                <>
                    <div className= "number"></div>
                    <img className="img" src={music.category.attributes.scheme}></img>
                    <div className="musicInfo">정보
                        <div className="name">{music.title.label}</div>
                        <div className="singer">가수</div>
                    </div>   
                </>
                )
            })}
        </div>
    )
}

export default Chat;
