import React, { useEffect, useState } from 'react';
import './App.scss';
import Detail from './Page/Detail/Detail';
import MusicChart from './Page/Chart/MusicChart';
import { Route, Routes } from 'react-router-dom';
import { musicData } from "./recoil/atom";
import { useRecoilState } from 'recoil';
import axios from 'axios';

function App() {
  const [data, setData] = useRecoilState(musicData);

  useEffect(() => {
    axios
      .get ('https://itunes.apple.com/us/rss/topalbums/limit=100/json',)
      .then((res) => {
          setData(res.data.feed.entry)
      })
      .catch((err) => {
          console.log(err)
      })  
  },[])

  const MusicDetail = (data:any) => {
    setData(data);
  };
  console.log(data)

  return (
    <div className="App">
      <header className="App-header">
        <Routes>
          <Route path='/' element={<MusicChart MusicDetail={MusicDetail}/>}/>
          <Route path='/detail/:musicId' element={<Detail data={data}/>}/>
        </Routes>
      </header>
    </div>
  );
}

export default App;
