import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import '../style.scss'

const Chat = () => {
    const [data, setData] = useState([]);
    const [search, setSearch] = useState(1);
    useEffect(() => {
      axios
        .get ('https://itunes.apple.com/us/rss/topalbums/limit=100/json',)
        .then((res) => {
            setData(res.data.feed.entry)
            console.log(res.data.feed.entry)
        })
        .catch((err) => {
            console.log(err)
        })  
    },[])
    // console.log(data[0]["im:image"][0].attributes.label)
    const searchMusic = (e:any) => {
      console.log(e.target.value) 
    }

    const submitSearch = (e:any) => {
        setSearch(e.target.value)
    }

    let sortUp = [...data]
    console.log(sortUp[0])
    const ascendingOrder = () => {
        sortUp.sort((a:any,b:any) => 
            a.title.label - b.title.label
            // if(a.title.label > b.title.label) return 1
            // if(a.title.label < b.title.label) return -1
            // return 0
        )
        setData(sortUp)
    }
    console.log(data[0])

    const descendingOrder = ()=> {
        sortUp.sort((a:any,b:any) => 
            b.title.label - a.title.label)
        // console.log(data)
        console.log(sortUp[0])
    }



    return(
        <div className="chat">
            MUSIC TOP 100
            <div className="search">
                <input
                    placeholder="찾으시는 음악 이름을 입력하세요"
                    onChange={searchMusic}
                />
                <button onClick={submitSearch}>검색</button>
            </div>
            <div className="sort">
                <button onClick={ascendingOrder}>오름차순</button>
                <button onClick={descendingOrder}>내림차순</button>
            </div>
            <div className="musics">
            <div>순위</div>
            {data && data.map((music:any) => {
                return(
                    <Link to={`/detail/${music.id.attributes["im:id"]}`}>
                        {/* <div>{music}</div><div className="musics"> */}
                        <div className="musicInfo">
                            {/* <div>{setNumber(number+1)}</div> */}
                            <img className="img" src={music["im:image"][0].label}></img>
                            <div className="name">{music.title.label}</div>
                        </div>   
                    </Link>
                )
            })}
            </div>
        </div>
    )
}

export default Chat;
