import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { musicData } from "../../recoil/atom";
import { useRecoilState, useSetRecoilState} from 'recoil';
import '../style.scss'

const MusicChart = (props:any) => {
    const [data, setData] = useRecoilState(musicData);
    const [search, setSearch] = useState("");
    // const setData = useSetRecoilState(musicData)
    const [count, setCount] = useState(0)
    const navigate = useNavigate();

    // useEffect(() => {
    //   axios
    //     .get ('https://itunes.apple.com/us/rss/topalbums/limit=100/json',)
    //     .then((res) => {
    //         setData(res.data.feed.entry)
    //     })
    //     .catch((err) => {
    //         console.log(err)
    //     })  
    // },[])

    console.log(props)
    // 검색
    const searchMusic = (e:any) => {
      setSearch(e.target.value)
    }

    const submitSearch = (e:any) => {
        setSearch(e.target.value)
    }

    let sortUp = [...data]

    //오름차순 정렬
    const ascendingOrder = () => {
        sortUp.sort((a:any,b:any) => 
            a.title.label.localeCompare(b.title.label)
        )
        setData(sortUp)
    }

    //내림차순 정렬
    const descendingOrder = ()=> {
        sortUp.sort((a:any,b:any) => 
        b.title.label.localeCompare(a.title.label))
        // console.log(data)
        setData(sortUp)
    }

    const goToDetail = () => {
        props.MusicDetail(data);
        // navigate(`/Detail/${data.id.attributes["im:id"]}`);
    }

    return(
        <div className="chart">
            <div className="search">
                <h3>ALBEM TOP 100</h3>
                <input
                    placeholder="찾으시는 음악 이름을 입력하세요"
                    onChange={searchMusic}
                />
                <button onClick={submitSearch}>검색</button>
            </div>
            <div className="sort">
                <button onClick={ascendingOrder}>오름차순</button>
                <button onClick={descendingOrder}>내림차순</button>
            </div>
            <div className="musics">
                <div className="line"/>
                <div className="title">
                    <h6>순위</h6>
                    <h6>곡-아티스트</h6>
                </div>

                {data.filter((music) => {
                    if(search === ""){
                    return music
                } else if(music.title.label.toLowerCase().includes(search.toLowerCase())){
                    return music
                }}).map((music:any) => {
                    return(
                    <>
                        <Link to={`/detail/${music.id.attributes["im:id"]}`} className="link">
                            <div onClick={goToDetail}>
                                <div className="musicInfo">
                                    <div>{count}</div>
                                    <img className="img" src={music["im:image"][0].label} alt=""></img>
                                    <div className="name">{music.title.label}</div>
                                </div>
                            </div>
                        </Link>
                    <div className="line2"/>   
                    </>
                    )
                })}
            </div>
        </div>
    )
}

export default MusicChart;
