import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import '../style.scss'

const Chat = () => {
    const [data, setData] = useState([]);
    useEffect(() => {
      axios
        .get ('https://itunes.apple.com/us/rss/topalbums/limit=100/json',)
        .then((res) => {
            setData(res.data.feed.entry)
            console.log(res.data.feed.entry[0])
        })
        .catch((err) => {
            console.log(err)
        })  
    },[])

    const searchMusic = () => {
      console.log() 
    }
    return(
        <div className="chat">
            MUSIC TOP 100
            <div className="search">
                <input
                    placeholder="찾으시는 음악 이름을 입력하세요"
                    onChange={searchMusic}
                />
                <button>검색</button>
            </div>
            <div className="musics">
            {data && data.map((music:any) => {
                return(
                    <Link to='/detail'>
                        {/* <div>{music}</div><div className="musics"> */}
                        <div className= "number"></div>
                        <img className="img" src={music.category.attributes.scheme}></img>
                        <div className="musicInfo">
                            <div className="name">{music.title.label}</div>
                            <div className="singer"></div>
                        </div>   
                    </Link>
                )
            })}
            </div>
        </div>
    )
}

export default Chat;
